#wallet and private key infos
wallet = ''
key = ''

def swapTokenForToken(amountIn,sellingToken,buyingToken):
    # first param howmuch amount you want to buy.
    tx = routerContract.functions.swapExactTokensForTokens(amountIn, 0, [w3.toChecksumAddress(sellingToken), w3.toChecksumAddress(buyingToken)], wallet, int(time.time()) + 3600).buildTransaction({
        'chainId': 43114,
        'gas': 210000,
        'gasPrice': w3.toWei(27, 'gwei'),
        'from': wallet,
        'nonce': w3.eth.getTransactionCount(wallet)
    })
    signedTx = w3.eth.account.signTransaction(tx, private_key=key)
    print('TRANSACTION SIGNED! ' +str(signedTx))
    return True
    #return bool(w3.eth.send_raw_transaction(signedTx.rawTransaction))  #if it is signed, transaction ddone
def signTransaction(txID):
    signedTx = w3.eth.account.signTransaction(txID, private_key=key)
    w3.eth.send_raw_transaction(signedTx.rawTransaction)

