def buyAUSD(amountIn):
    isTransactionOK = True
    print('MIM more valuable than Ausd! BUY AUSD FOR MIM ' )
    isTransactionOK = swapTokenForToken(amountIn,  mimAddress,  aUSDAddress ) #we are buying Ausd for MIM 
    return isTransactionOK

# asynchronous defined function to loop
# this loop checks mim-ausd pair value and if it sees %10 arbitrage it swaps tokens
# this loop runs on a poll interval
async def log_loop(amounts, poll_interval):
    while True:
        mimAmount = amounts[0] 
        aUsdAmount = amounts[1] #ausd value rn  1021240504731500103
        #100000000000000000 %10 percent of 1000000000000000000
        mim110PercentValue = mimAmount + (mimAmount//10)
        mim90PercentValue = mimAmount - (mimAmount//10)
        print('MIM VALUE : ' +str(mimAmount))  
        print('1 MIM EQUALS TO AUSD VALUE : ' +str(aUsdAmount))
            
        if(aUsdAmount < mim90PercentValue and buyMIM(mimAmount) is True):
           print('MIM bought') 
           mintAUSD(mimAmount) 
           break
        
        if(aUsdAmount > mim110PercentValue and buyAUSD(aUsdAmount) is True):
            print('AUSD bought')
            redeemAUSD(aUsdAmount)
            break 
        print('##########')    
        await asyncio.sleep(poll_interval)


# when main is called
# retrieve AUSD value for 1 MIM
# run an async loop
# try to run the log_loop function above every 2 seconds
def main():
    #price = w3.toWei(1, 'ether')  #that means 1 token 
    amounts =  routerContract.functions.getAmountsOut(howMuchMoneyArbitrage, [w3.toChecksumAddress(mimAddress), w3.toChecksumAddress(aUSDAddress)]).call();
    loop = asyncio.get_event_loop()
    try:
        loop.run_until_complete(
            asyncio.gather(
                log_loop(amounts, 2)))
                
    finally:
        # close loop to free up system resources
        loop.close()


if __name__ == "__main__":
    main()